/* eslint-disable @typescript-eslint/ban-types */
import axios from 'axios';
import AppError from '../errors/AppError';

interface Recipe {
  title: string;

  ingredients: Array<string>;

  href: string;

  thumbnail: string;
}

async function getOneGif(keywords: string | undefined): Promise<Array<Recipe>> {
  try {
    const recipes = await axios
      .get(`${process.env.BASE_URL_RECIPES}?i=${keywords}`)
      .then(res => res.data.results);

    return recipes;
  } catch (error) {
    throw new AppError({ message: error.message, statusCode: 400 });
  }
}

export default getOneGif;

interface DataError {
  message: string;
  statusCode: number;
}

export default class Error {
  public readonly message: string;

  public readonly statusCode: number;

  constructor({ message, statusCode }: DataError) {
    this.message = message;
    this.statusCode = statusCode;
  }
}

import axios from 'axios';
import AppError from '../errors/AppError';

async function getOneGif(title: string): Promise<string> {
  try {
    const gif = await axios
      .get(
        `${process.env.BASE_URL_GIFS}?api_key=${
          process.env.API_KEY_GIPHY
        }&q=${title.trim()}`,
      )
      .then(response => response.data.data[0].url);

    return gif;
  } catch (error) {
    throw new AppError({ message: error.message, statusCode: 400 });
  }
}

export default getOneGif;

/* eslint-disable no-return-await */
/* eslint-disable no-await-in-loop */
/* eslint-disable no-plusplus */
import AppError from '../errors/AppError';
import Recipe from '../models/Recipe';
import getRecipes from './GetRecipesService';
import getOneGif from './GetOneGifService';

// eslint-disable-next-line @typescript-eslint/no-var-requires
require('dotenv').config();

async function formatRecipe(
  keywords: string | undefined,
): Promise<Array<Recipe>> {
  try {
    const recipesFormated: Array<Recipe> = [];

    const recipes = await getRecipes(keywords);
    await Promise.all(
      recipes.map(async recipe =>
        recipesFormated.push(
          new Recipe({
            title: recipe.title.trim(),
            ingredients: recipe.ingredients
              .toString()
              .split(',')
              .map(ingredient => ingredient.trim())
              .sort((a, b) => {
                if (a < b) return -1;
                if (a > b) return 1;
                return 0;
              }),
            link: recipe.href,
            gif: await getOneGif(recipe.title),
          }),
        ),
      ),
    );

    return recipesFormated;
  } catch (error) {
    throw new AppError({
      message: error.message,
      statusCode: 400,
    });
  }
}

export default formatRecipe;

class Recipe {
  title: string;

  ingredients: Array<string>;

  link: string;

  gif: string;

  constructor({ title, ingredients, link, gif }: Recipe) {
    this.title = title;
    this.ingredients = ingredients;
    this.link = link;
    this.gif = gif;
  }
}

export default Recipe;
